ai = new Ai();

eventChat = function(sender, to, message) {
  var obj;
  obj = {
    name: 'Chat',
    sender: sender,
    to: to,
    message: message
  };
  return ai.events(obj);
};

eventDestroyed = function(object) {
  var found, group, obj;
  if (object.name !== 'Oil Resource') {
    group = null;
    if (object.player === me && (found = ai.groups.finds(object))) {
      group = found.group;
      object = found.object;
      group.list.removeObject(object);
    } else {
      object = new WZObject(object);
    }
    obj = {
      name: 'Destroyed',
      object: object,
      group: group
    };
    return ai.events(obj);
  }
};

eventDroidBuilt = function(droid, structure) {
  var found, obj;
  found = ai.groups.finds(structure);
  obj = {
    name: 'DroidBuilt',
    droid: new WZObject(droid),
    structure: found.object,
    group: found.group
  };
  return ai.events(obj);
};

eventDroidIdle = function(droid) {
  var found, obj;
  found = ai.groups.finds(droid);
  obj = {
    name: 'DroidIdle',
    droid: found.object,
    group: found.group
  };
  return ai.events(obj);
};

eventResearched = function(research, structure) {
  var found, obj;
  found = ai.groups.finds(structure);
  obj = {
    name: 'Researched',
    research: research,
    structure: found != null ? found.object : void 0,
    group: found != null ? found.group : void 0
  };
  return ai.events(obj);
};

eventStartLevel = function() {
  var obj;
  obj = {
    name: 'StartLevel'
  };
  return ai.events(obj);
};

eventStructureBuilt = function(structure, droid) {
  var found, obj;
  found = ai.groups.finds(droid);
  obj = {
    name: 'StructureBuilt',
    structure: new WZObject(structure),
    droid: found.object,
    group: found.group
  };
  return ai.events(obj);
};

